import { useNavigate } from "react-router-dom";
import { useEffect, useRef } from "react";
import axios from "axios";

//images
import { ReactComponent as ErrorIcon } from "../imgs/error.svg"
import { ReactComponent as CheckmarkIcon } from "../imgs/checkmark.svg"


export default function RegistrationPage() {

    const form = useRef(null);
    const btn = useRef(null);
    const email = useRef(null);
    const username = useRef(null);
    const password = useRef(null);
    const confirm = useRef(null);
    const errorIcon = useRef(null);
    const checkmarkIcon = useRef(null);

    useEffect(()=>{
        form.current.style.marginLeft = `-${form.current.offsetWidth/2}px`;
        form.current.style.marginTop = `-${form.current.offsetHeight/2}px`;
        btn.current.style.gridColumn = `span 20`;
    })

    const createAccount = (e) => {
        e.preventDefault();
        let request = {
            username: username.current.value,
            email: email.current.value,
            password: password.current.value
        }
        axios.post("http://localhost:8080/api/registration",request)
        .then((response)=>{
            console.log(response);
        });
    }

    const matchPassword = (e) => {
        if (confirm.current.value === "") {
            checkmarkIcon.current.style.display="none";
            errorIcon.current.style.display="none";
            return
        }
        if (password.current.value === confirm.current.value) {
            checkmarkIcon.current.style.display="block";
            errorIcon.current.style.display="none";
        } else {
            checkmarkIcon.current.style.display="none";
            errorIcon.current.style.display="block";
        }
    }

    return (

        <div className="login--form" ref={form} >
            <div className="par">
                <p>Username</p>
            </div>
            <div className="img">
                
            </div>
            <div className="inp">
                <input type="text" ref={username} ></input>
            </div>
            <div className="par">
                <p>E-Mail</p>
            </div>
            <div className="img">
                
            </div>
            <div className="inp">
                <input type="text" ref={email} ></input>
            </div>
            <div className="par">
                <p>Password</p>
            </div>
            <div className="img">
                
            </div>
            <div className="inp">
                <input type="text" ref={password} onChange={matchPassword}></input>
            </div>
            <div className="par">
                <p>Confirm Password</p>
            </div>
            <div className="img">
                <ErrorIcon ref={errorIcon} display="none" height="40px" width="40px"/>
                <CheckmarkIcon ref={checkmarkIcon} display="none" height="40px" width="40px"/>
            </div>
            <div className="inp">
                <input type="text" ref={confirm} onChange={matchPassword} ></input>
            </div>
            <div className="btn" ref={btn} >
                <button onClick={createAccount}>Create Account</button>
            </div>
        </div>

    )
}
