import { useNavigate } from "react-router-dom";
import { useEffect, useRef } from "react";
import axios from "axios";

export default function LoginPage() {


    const username = useRef(null);
    const password = useRef(null);
    const form = useRef(null);
    const navigate = useNavigate();

    useEffect(()=>{
        form.current.style.marginLeft = `-${form.current.offsetWidth/2}px`;
        form.current.style.marginTop = `-${form.current.offsetHeight/2}px`;
    });

    const registerButton = (e) => {
        e.preventDefault();
        navigate("/registration");
    }

    const loginButton = (e) => {
        e.preventDefault();
        axios.get("http://localhost:8080/api/login")
    }


    return (

        <div className="login--form" ref={form} >
            <div className="par">
            <p>Username</p>
            </div>
            <div className="img">
            </div>
            <div className="inp">
            <input type="text" ref={username} ></input>
            </div>
            <div className="par">
            <p>Password</p>
            </div>
            <div className="img">
            </div>
            <div className="inp" ref={password} >
            <input type="text"></input>
            </div>
            <div className="btn">
            <button onClick={loginButton} >Login</button>
            </div>
            <div className="btn">
            <button onClick={registerButton} >Register</button>
            </div>
        </div>

    );
}
