import { useEffect, useRef, useState } from "react"
import { useNavigate } from "react-router-dom";

export default function MessageDefaultRedirect(props) {

    const navigate = useNavigate();
    const mainRect = useRef(null);
    const [counter,setCounter] = useState(10);

    useEffect(() => {
        mainRect.current.style.marginLeft = `-${mainRect.current.offsetWidth/2}px`;
        mainRect.current.style.marginTop = `-${mainRect.current.offsetHeight/2}px`;
    });

    useEffect(() => {
        const interval = setInterval(()=>{
            setCounter(counter--);
        },1000);

        return () => clearInterval(interval);
    },[]);

    useEffect(() => {
        if (counter ===0){
            navigate("/");
        }
    },[counter]);


    return (
        <div className="message--default--redirect" ref={mainRect}>
            <p>{props.message}</p>
            <p>You will be redirected to the login page in {counter}. Click <link href="http://localhost:3000">here</link> to go to the login page now.</p>
        </div>
    );
}
