import { useNavigate } from "react-router-dom";
import { useEffect, useRef } from "react";

export default function RegistrationPage() {

    const form = useRef(null);
    const btn = useRef(null);

    useEffect(()=>{
        form.current.style.marginLeft = `-${form.current.offsetWidth/2}px`;
        form.current.style.marginTop = `-${form.current.offsetHeight/2}px`;
        btn.current.style.gridColumn = `span 20`;
    })

    return (

        <div className="login--form" ref={form} >
            <div className="par">
            <p>Username</p>
            </div>
            <div className="inp">
            <input type="text"></input>
            </div>
            <div className="par">
            <p>E-Mail</p>
            </div>
            <div className="inp">
            <input type="text"></input>
            </div>
            <div className="par">
            <p>Password</p>
            </div>
            <div className="inp">
            <input type="text"></input>
            </div>
            <div className="par">
            <p>Confirm Password</p>
            </div>
            <div className="inp">
            <input type="text"></input>
            </div>
            <div className="btn" ref={btn} >
            <button>Register</button>
            </div>
        </div>

    )
}
