import { useNavigate } from "react-router-dom";
import { useEffect, useRef } from "react";

export default function LoginPage() {


    const form = useRef(null);
    let navigate = useNavigate();

    useEffect(()=>{
        form.current.style.marginLeft = `-${form.current.offsetWidth/2}px`
        form.current.style.marginTop = `-${form.current.offsetHeight/2}px`
    })

    const routeChange = (e) => {
        navigate(e.target.id);
    }


    return (

        <div className="login--form" ref={form} >
            <div>
            <p>Username:</p>
            </div>
            <div>
            <input type="text"></input>
            </div>
            <div>
            <p>Password:</p>
            </div>
            <div>
            <input type="text"></input>
            </div>
            <div>
            <button>Login</button>
            </div>
            <div>
            <button onClick={routeChange} id="/registration" >Register</button>
            </div>
        </div>

    )
}
